import React, { useState, useEffect, useCallback} from 'react';
import { SafeAreaView, FlatList,StatusBar, ScrollView, Dimensions, Text, View, ActivityIndicator, StyleSheet} from 'react-native';
import { useNavigation} from '@react-navigation/native';
import LottieView from 'lottie-react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import Posts from './Posts';
 import client from '../api/client'
import { COLORS } from '../constants/theme';
import DailyTips from "../Components/DailyTips"
import ErrorButton from '../Components/ErrorButton';
const {width, height} = Dimensions.get("screen")

const PAGE_SIZE = 10;

const HomeScreen = () => {
  const navigation = useNavigation();
  
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
   const [userInfo, setUserInfo] = useState(null);
  const [userToken, setUserToken] = useState(null);
  const [hasMoreData, setHasMoreData] = useState(true);
  const [error,setError] = useState(false)
  const [errorMessage, setErrorMessage] = useState("")
  const [cacheExpiry, setCacheExpiry] = useState(null);
  const [shouldLoadMorePosts, setShouldLoadMorePosts] = useState(false);


  const getPostData = async () => {

    setIsLoading(true);

    try {
      const userToken = await AsyncStorage.getItem("userToken");
          const value = await AsyncStorage.getItem("userInfo")
      if (userToken !== null && value !== null) {
             const userInfo = JSON.parse(value)
        setUserToken(userToken);
        setUserInfo(userInfo);

const token = userToken
        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
					 "content-type": "multipart/form-data",
          },
        };

        const res = await client.get(
          `/news/get${userInfo.campus}CampusNews/${currentPage}/${PAGE_SIZE}`,
          config
        );
              
        console.log (res.data.data)

        const responseData = res.data.data;

        if (responseData.length === 0) {
          setHasMoreData(false);
          setIsLoading(false)
          // return;
        }
       setPosts(prevPosts => [...prevPosts, ...responseData]);
    }
    } catch (e) {
      console.log(`${e}`);
      console.log(e)
      setError(true);
      setErrorMessage( e.message ? e.message : "Oops! Something went wrong. Please try again later.");
  
    } finally {
      setIsLoading(false);
    }  
  };



  
  useEffect(()=>{
    getPostData()
  },[currentPage])


  const loadMorePosts = async () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };
  

  const renderLoader =()=>{
    return(
    isLoading ?
    <View style={{marginVertical:80, alignItems:"center"}}>
       <LottieView
          source={require('../assets/animations/loader.json')}
          style={{
            width: 50,
            height: 50,
            alignSelf: 'center',
          }}
          loop
          speed={0.7}
          autoPlay
        />
    </View>
    : null
    )
  }

  const renderItem = useCallback(
    ({ item }) => (
        <Posts post={item} key={item.id} navigation={navigation}/>
    ),
    [navigation]
  );





   const handleRefresh = useCallback(async () => {
    try {
      setPosts([]);
      setCurrentPage(1); // Reset currentPage to 1 when refreshing
      setIsLoading(true);

      await getPostData();
      // setCacheExpiry(null); 
      setIsLoading(false);
    } catch (e) {
      console.log(e);
      setError(true)
      setErrorMessage("An error occured")
    }
  }, [getPostData]);

const renderHeader =()=>{
  return(
    <View>
      <DailyTips/>
    </View>
  )
}

  return (
    <SafeAreaView style={{ flex: 1, paddingTop:50}}>
      <StatusBar backgroundColor={COLORS.darkgray}/>
       {posts.length === 0 && !isLoading && (
         <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
        <Text style={{fontFamily:"Poppins"}}>No posts present</Text>
      </View>
    )}
      <FlatList
      //  removeClippedSubviews
      ListHeaderComponent={renderHeader}
      onEndReachedThreshold={0.1}
      onEndReached={loadMorePosts}
      showsVerticalScrollIndicator={false}
      data={posts}
      bounces={false}
      decelerationRate={'fast'}
      ListFooterComponent={renderLoader}
      renderItem={renderItem}
      keyExtractor={(item) => item._id}
      refreshing={isLoading && posts.length === 0}
      onRefresh={handleRefresh}
      />
      {error && <ErrorButton onPress={() =>{ setError(false); getPostData();}}message={errorMessage} style={{paddingTop:height*0.48}} color= {COLORS.red} borderRadius={10}/>}
    </SafeAreaView>
  );
};

export default HomeScreen;
